<?php/************************************************* *		functions_bbcode.php *		--------------------- *		作者：爱疯的云 *		描述：适用于：index.php，page.php 页面 *			建议不要将本 BBcode 用到其他页面 *************************************************/ if ( !defined('IN_PHPBB') ){	die("Hacking attempt");}function page_bbcode($text){	global $board_config, $page_title, $phpbb_root_path, $lang, $phpEx;				$text = str_replace("[page_title]", $page_title, $text);		$text = str_replace("[sitename]", $board_config['sitename'], $text);		$text = str_replace("[sitedesc]", $board_config['site_desc'], $text);				$text = preg_replace("/\[page=([0-9]*)\]/si", '<a href="' . append_sid("page.$phpEx?page=\\1") . '">', $text);		$text = str_replace('[/page]', '</a>', $text);						$text = preg_replace("/\[div class=(.*?)\]/si", '<div class="\\1">', $text);		$text = str_replace('[/div]', '</div>', $text);		return $text;}?>