<?php/*************************************************** *		usercp_guestbook.php *		-------------------- *		作者：爱疯的云 *		说明：留言板功能 **************************************************/if ( !defined('IN_PHPBB') ){	die("Hacking attempt");	exit;}if ( empty($HTTP_GET_VARS[POST_USERS_URL]) || $HTTP_GET_VARS[POST_USERS_URL] == ANONYMOUS ){	message_die(GENERAL_MESSAGE, $lang['No_user_id_specified']);}$profiledata = get_userdata($HTTP_GET_VARS[POST_USERS_URL]);if (!$profiledata){	message_die(GENERAL_MESSAGE, $lang['No_user_id_specified']);}$page_title = '留言板';include($phpbb_root_path . 'includes/page_header.'.$phpEx);include($phpbb_root_path . 'includes/functions_guestbook.'.$phpEx);if(isset($HTTP_GET_VARS['gb'])){	$gb = new guestbook($profiledata,$HTTP_GET_VARS['gb']);}else{	$gb = new guestbook($profiledata,'view');}display_upload_attach_box_limits($profiledata['user_id']);$template->set_filenames(array(	'body' => 'profile_guestbook_body.tpl'));$template->pparse('body');include($phpbb_root_path . 'includes/page_tail.'.$phpEx);?>